import React, { useState } from "react";

const ChatHeader = ({title,onDelete}) => {
  const [showModal, setShowModal] = useState(false);
  const handleDeleteClick = (e) => {
    e.preventDefault();
    setShowModal(true);
  };

  const handleConfirmDelete = () => {
    onDelete();
    setShowModal(false);
  };

  const handleCancelDelete = () => {
    setShowModal(false);
  };


    return (
        <>
    <div style={headerStyle}>
      <div style={titleContainerStyle}>
        <div style={titleStyle}>{title}</div>
      </div>
      <div style={buttonContainerStyle}>
        {onDelete&&(<button style={buttonStyle} onClick={handleDeleteClick}>Delete</button>)}
      </div>
    </div>
    {showModal && (
        <div>
          <div style={overlayStyle}></div>
          <div style={modalStyle}>
            <p>Are you sure you want to delete?</p>
            <div style={buttonContainerStyle}>
              <button style={confirmButtonStyle}  onClick={handleConfirmDelete}>Yes</button>
              <button style={cancelButtonStyle}  onClick={handleCancelDelete}>No</button>
            </div>
          </div>
        </div>
      )}
        </>
    );
}



const modalStyle = {
  position: "fixed",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  padding: "20px",
  width: "400px",
  backgroundColor: "#282c34",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  zIndex: 1000,
  textAlign: "center", 
};

const overlayStyle = {
  position: "fixed",
  top: 0,
  left: 0,
  width: "100%",
  height: "100%",
  backgroundColor: "rgba(0, 0, 0, 0.5)",
  zIndex: 999,
};


const modalButtonContainerStyle = {
  marginTop: "20px", // Add some space between buttons and modal content
  alignItems:"center"
};

const confirmButtonStyle = {
  marginRight: '10px',
  padding: '10px 15px',
  backgroundColor: '#61dafb',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '14px',
};

const cancelButtonStyle = {
  padding: '10px 15px',
  backgroundColor: '#e74c3c',
  color: '#fff',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '14px',
};

const headerStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px',


    // backgroundColor: '#eee',
  };

  const titleContainerStyle = {
    flex: '1', // This makes the title container take up the available space
    textAlign: 'center', // Center the title horizontally
  };

  const titleStyle = {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    paddingBottom:'5px'
  };

  const buttonContainerStyle = {
    display: 'flex',
    marginRight:'10px',
  };

  const buttonStyle = {
    marginLeft: '10px',
    padding: '10px 15px',
    backgroundColor: '#202022', 
    color: '#d1d5db', 
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '14px',
  };


export default ChatHeader;
