import React, { useEffect, useRef, useState } from "react";
import { useChat } from "../../contexts/ChatContext";
import ChatPrompt from "./ChatLog/ChatPrompt";
import BotMessage from "./ChatLog/BotMessage";

const ChatLog = () => {
const {selectedChat} = useChat();
const [currentSelectedChat,setCurrentSelectedChat] = useState(null);
const [currentChatLogs,setCurrentChatLogs] = useState(null);
const chatLogRef = useRef(null);

useEffect(()=>{
    if (currentSelectedChat === null || currentSelectedChat.chat_id !== selectedChat.chat_id){
        setCurrentSelectedChat(selectedChat);
        const logs = selectedChat.logs.map((c)=>{
          c.new = false
          return c;
        })
        console.log(logs);
        setCurrentChatLogs(logs);
        return;
    }

    const isNewLog = selectedChat.logs.length !== currentChatLogs.length;
    if(isNewLog){
      selectedChat.logs.map((c)=>{
        if ([...currentChatLogs.map((item)=>item.prompt_id)].includes(c.chat_id)){
          c.new = false;
        }else{

        }
      })
    }


},[selectedChat])

useEffect(() => {
    if (chatLogRef.current) {
      chatLogRef.current.scrollIntoView({
        behavior: "smooth",
        block: "end",
      });
    }
    return () => {};
  }, []);


  return (
    <div className="chatLogWrapper">
        {currentChatLogs && Array.isArray(currentChatLogs) && currentChatLogs.length > 0 && currentChatLogs.map((chat, idx) => {
          console.log(currentChatLogs);
          console.log("CHAT INDEX:",idx,"CHAT:",chat)
            return (
            <div ref={chatLogRef} className="chatLog" key={`msg_${idx}`}>
                <ChatPrompt text={chat.prompt} />
                <BotMessage chatLogRef={chatLogRef} response={chat.response} animate={selectedChat.logs.length - 1 === idx} />
            </div>
        )})}
    </div>
  );
};

export default ChatLog;
