    const {loading} = useChat();
    return (
        <>{loading ? (<></>) : (<> 
                <aside className="sideMenu">
            <NewChatButton />
            <NavChatList/>
            {/* <NavLinksContainer chatLog={chatLog} setChatLog={setChatLog} /> */}
        </aside>
        </>)}</>

    );
}
export default LeftMenu;
