import React, { useEffect, useRef } from "react";
import ChatBoxInput from "./ChatInput/ChatInput";
import IntroSection from "./IntroSection";
import ChatPrompt from "./ChatLog/ChatPrompt";
import {useChat} from '../../contexts/ChatContext'
import NewChat from "./NewChat";
import BotMessage from "./ChatLog/BotMessage";
import ChatLog from "./ChatLog";
import ChatHeader from "./ChatHeader/ChatHeader";

const ChatBox = (props) => {
    const {selectedChat} = useChat();
    const chatLogRef = useRef(null);

    useEffect(() => {
        if (chatLogRef.current) {
          chatLogRef.current.scrollIntoView({
            behavior: "smooth",
            block: "end",
          });
        }
        return () => {};
      }, []);

    return (
        <>
        <section className="chatBox">
            <ChatHeader title={selectedChat.title}/>
            {selectedChat && selectedChat.id === null ? (<>
                <NewChat/>
            </>) : (<>
                {selectedChat.id === -1 ? (<>
                    <IntroSection/>
                </>) : (<>
                    <ChatLog/>
                </>)}
            </>)}
            <ChatBoxInput />
        </section>
        </>
    )
    return (
        <section className="chatBox">


            {selectedChat.title}
            {selectedChat.log.length > 0 ? (
                <div className="chatLogWrapper">
                    {selectedChat.log.length > 0 &&
                    selectedChat.log.map((chat, idx) => {
                        return (
                        <div
                        className="chatLog"
                        key={`msg_${idx}`}
                        ref={chatLogRef}
                        >
                            <ChatPrompt text={chat.prompt} />
                            <BotMessage chatLogRef={chatLogRef} response={chat.response} animate={selectedChat.log.length - 1 === idx} />

                        </div>

                    )})}
                </div>
            ) : (
                <>
                {showIntro?<IntroSection/>:<NewChat/>}
                </>
            )}
            <ChatBoxInput />
        </section>
    );




};



export default ChatBox;
