import React, { useEffect, useRef, useState } from "react";
import { useChat } from "../../contexts/ChatContext";

const ChatLog = () => {
const {selectedChat} = useChat();
const [currentSelectedChat,setCurrentSelectedChat] = useState(null);
const [currentChatLogs,setCurrentChatLogs] = useState(null);
const chatLogRef = useRef(null);

useEffect(()=>{
    if (currentSelectedChat === null || currentSelectedChat.id !== selectedChat.id){
        setCurrentSelectedChat(selectedChat);
        setCurrentChatLogs(selectedChat.log.map((c)=>{c.new = false}));
        return;
    }
    if (currentSelectedChat.id !== selectedChat.id){
        const isNewLog = selectedChat.log.length !== currentChatLogs.length;


    }

},[selectedChat])

useEffect(() => {
    if (chatLogRef.current) {
      chatLogRef.current.scrollIntoView({
        behavior: "smooth",
        block: "end",
      });
    }
    return () => {};
  }, []);


  return (
    <div className="chatLogWrapper">
        {currentChatLogs.length > 0 &&
        currentChatLogs.map((chat, idx) => {
            return (
            <div ref={chatLogRef} className="chatLog" key={`msg_${idx}`}>
                <ChatPrompt text={chat.prompt} />
                <BotMessage chatLogRef={chatLogRef} response={chat.response} animate={selectedChat.log.length - 1 === idx} />
            </div>
        )})}
    </div>
  );
};

export default ChatLog;
